mapper.select:
SELECT * FROM ML_IMG
WHERE ML_IMG_NO = #{movelineNo}
ORDER BY ML_IMG_LEVEL

mapper.delete:
UPDATE MOVELINE_BOARD SET
ML_DELETE_DT = SYSDATE
WHERE ML_NO = #{movelineNo}

mapper.select:
SELECT COUNT(*)
FROM
BOOKMARK_ML
WHERE USER_NO = #{loginNo}
AND ML_NO = #{movelineNo}

mapper.delete:
DELETE FROM BOOKMARK_ML
WHERE USER_NO = #{loginNo}
AND
ML_NO = #{movelineNo}

mapper.insert:

	SELECT SEQ_ML_HASH_NO.NEXTVAL FROM DUAL

INSERT INTO MOVELINE_HASH VALUES(
#{hashTagNo},
#{MLHashTag},
#{movelineNo}
)

INSERT INTO ML_IMG SELECT SEQ_ML_IMG_NO.NEXTVAL IMG_NO, A.* FROM(
SELECT #{img.movelineRename} ML_IMG_RENAME, #{img.movelineLevel} ML_IMG_LEVEL, #{img.movelineNo} ML_NO FROM DUAL
) A
